import React from "react";
import ReactDOM from "react-dom";
import Stack from "@mui/material/Stack";
import Button from "@mui/material/Button";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import DialogContentText from "@mui/material/DialogContentText";
import DialogTitle from "@mui/material/DialogTitle";
import useMediaQuery from "@mui/material/useMediaQuery";
import { useTheme } from "@mui/material/styles";
import { IconButton } from "@mui/material";
import CloseIcon from "@mui/material/Icon";

function BackApp(classes) {
  // Used for setting up the importing of class names
  var arrButtons = [];
  var stacks = [];
  // Needed to open and close the tutor window
  const [open, setOpen] = React.useState(false);
  const theme = useTheme();
  const fullScreen = useMediaQuery(theme.breakpoints.down('md'));
  const [title, setTitle] = React.useState("");
  function SetTitle(classname){
    console.log(classname);
    setTitle(classname);
    console.log(title);
  }
  const handleClickOpen = (classname) => {
    setOpen(true);
    SetTitle(classname);
  };
  const handleClose = () => {
    setOpen(false);
  };
  var i = 0;
  while (i < classes){
    console.log("i = " + i);
    arrButtons.push(
      <Button variant="contained" onClick={handleClickOpen}>
        {classes.at(i)}
      </Button>
    );
    if ((i + 1) % 3 == 0 || i + 1 == classes.length) {
      stacks.push(
        <Stack
          direction={{ xs: "column", sm: "row" }}
          spacing={{ xs: 1, sm: 2, md: 4 }}
          justifyContent="center"
          alignItems="center"
        >
          {arrButtons}
        </Stack>
      );
      arrButtons = [];
    }
    i++;
  }
  return (
    <div>
      <Stack direction="column" spacing={{ xs: 1, sm: 2, md: 2.5 }}>
        {stacks} //Very important to wrap the buttons inside a div
      </Stack>
    </div>
  );
  return (
    <div className="Classes">
      {/* Class Buttons */}
      <Stack direction="column" spacing={{ xs: 1, sm: 2, md: 2.5 }}>
        {stacks} //Very important to wrap the buttons inside a div
      </Stack>
      {/* Window Part */}
      <Dialog
        fullScreen={fullScreen}
        open={open}
        onClose={handleClose}
        aria-labelledby="Tutor Window"
      >
        <DialogTitle id="Tutor Window">
          {"Pick your tutor for " + title}
        </DialogTitle>
        <DialogContent>
          <DialogContentText>
            If you're seeing this, that means that the dialog box is working.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <IconButton aria-label="close">
            <CloseIcon />
          </IconButton>
        </DialogActions>
      </Dialog>
    </div>
  );
}

export default BackApp;
